import { Service } from '../utils/service.js';
export function createStop({network, preload, ipns, repo, mfsPreload}) {
  const stop = async () => {
    await Promise.all([
      preload.stop(),
      ipns.stop(),
      mfsPreload.stop(),
      Service.stop(network),
      repo.close()
    ]);
  };
  return stop;
}